import React, { useState } from 'react';
import { motion } from 'framer-motion';

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [phone, setPhone] = useState('');

  const inputVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } },
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center relative bg-purple-900">
      <motion.div
        className="absolute inset-0 bg-cover bg-center z-0"
        style={{ backgroundImage: `url('/rec.jpeg')`, minHeight: '100%', minWidth: '100%' }}
        initial={{ scale: 1 }}
        animate={{ scale: 1.1 }}
        transition={{ duration: 15, repeat: Infinity, repeatType: "reverse" }}
      ></motion.div>
      <div className="absolute inset-0 bg-purple-800 opacity-75 z-0" style={{ minHeight: '100%', minWidth: '100%' }}></div>
      <div className="relative z-10 mb-8">
        <img src="/mnt/data/logo2.png" alt="Rajalakshmi Engineering College" className="w-64 lg:w-80" />
      </div>
      <div className="relative z-10 bg-white p-8 rounded-lg shadow-lg max-w-md w-full lg:max-w-2xl mx-auto">
        <h1 className="text-3xl font-bold mb-6 text-purple-900 text-center">Register</h1>
        <motion.div initial="hidden" animate="visible" variants={inputVariants} className="mb-4">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full p-3 border border-purple-300 rounded bg-purple-50 focus:outline-none focus:border-purple-500 hover:shadow-lg transition duration-200"
          />
        </motion.div>
        <motion.div initial="hidden" animate="visible" variants={inputVariants} className="mb-4">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full p-3 border border-purple-300 rounded bg-purple-50 focus:outline-none focus:border-purple-500 hover:shadow-lg transition duration-200"
          />
        </motion.div>
        <motion.div initial="hidden" animate="visible" variants={inputVariants} className="mb-4">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-3 border border-purple-300 rounded bg-purple-50 focus:outline-none focus:border-purple-500 hover:shadow-lg transition duration-200"
          />
        </motion.div>
        <motion.div initial="hidden" animate="visible" variants={inputVariants} className="mb-4">
          <input
            type="text"
            placeholder="Phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            className="w-full p-3 border border-purple-300 rounded bg-purple-50 focus:outline-none focus:border-purple-500 hover:shadow-lg transition duration-200"
          />
        </motion.div>
      </div>
    </div>
  );
}

export default RegisterPage;
