// src/components/LoginPage.jsx
import React from 'react';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import './LoginPage.css'; // Import your CSS file

const LoginPage = () => {
  const handleSuccess = (response) => {
    console.log(response);
    // Handle Google login response here
  };

  const handleError = (error) => {
    console.error(error);
    // Handle Google login error here
  };

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <div className="flex h-screen bg bg-slate-300">
        <div className="flex items-start justify-start">
          <img src="/logo2.png" alt="Logo" className="w-90 h-auto justify-start" />
        </div>
        {/* Login Form */}
        <div className="w-1/2 bg-grey flex flex-col justify-center items-center p-8">
          <h1 className="text-3xl font-bold mb-6">LOGIN</h1>
          <form className="w-full">
            <div className="mb-4">
              <label htmlFor="username" className="block text-sm font-medium text-gray-700">
                Username
              </label>
              <input
                type="text"
                id="username"
                className="mt-1 p-2 w-full border border-gray-300 rounded-md shadow-sm"
                placeholder="Enter your username"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                Password
              </label>
              <input
                type="password"
                id="password"
                className="mt-1 p-2 w-full border border-gray-300 rounded-md shadow-sm"
                placeholder="Enter your password"
              />
            </div>
            <button
              type="submit"
              className="w-full py-2 px-4 bg-blue-500 text-white rounded-md hover:bg-blue-600"
            >
              Login
            </button>
            <div className="my-4 flex items-center justify-between">
              <span className="block w-full border-t border-gray-300"></span>
              <span className="mx-2 text-gray-500">or</span>
              <span className="block w-full border-t border-gray-300"></span>
            </div>
            <GoogleLogin
              onSuccess={handleSuccess}
              onError={handleError}
              className="w-full"
            />
          </form>
        </div>
        {/* Background Image */}
        <div className="flex justify-end opacity-350">
          <img
            src="https://www.rajalakshmi.org/image/banner-1.jpg"
            className="w-4/4 object-cover h-full"
            alt="Description"
          />
        </div>
        <div className="bg-[radial-gradient(ellipse_at_top_left,_var(--tw-gradient-stops))] from-purple-300 via-gray-100 to-blue-300">
        </div>
      </div>
    </GoogleOAuthProvider>
  );
};

export default LoginPage;
