import React, { useState } from 'react';
import logo from '../public/logo2.png'; // Import the logo image
import { motion } from 'framer-motion';

const RegistrationPage = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Form submitted:', { username, email, password });
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.5 } },
    exit: { opacity: 0, transition: { duration: 0.5 } }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5 } }
  };

  return (
    <motion.div
      initial="hidden"
      animate="visible"
      exit="exit"
      variants={containerVariants}
      className="flex flex-col items-center justify-center min-h-screen bg-gray-100"
    >
      <div className="relative z-10 mb-8"> {/* Add closing tag here */}
        <img src="/logo2.png" alt="Rajalakshmi Engineering College" className="w-64 lg:w-80" />
      </div>

      <img src={logo} alt="Rajalakshmi Engineering College Logo" className="mb-8" />

      <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
        <motion.div className="mb-4" variants={itemVariants}>
          {/* ... rest of your form elements ... */}
        </motion.div>
        {/* ... other form elements ... */}
      </form>
    </motion.div>
  );
};

export default RegistrationForm;
